class Contracts::ContractLlcDraftUkraineService
  def generate
    Template.find_or_create_by(name: 'contract-llc-draft-ukraine') do |template|
      template.body = ".contract-llc-draft\r\n  %p.center.header= \"AGREEMENT # \#{contract.number} on provision of services\"\r\n  %br\r\n  %table\r\n    %tr\r\n      %td\r\n        %p.bold.center ДОГОВІР\r\n        %p.bold.center про надання послуг\r\n      %td\r\n        %p\r\n        %p.bold.center AGREEMENT\r\n        %p.bold.center on provision of services\r\n    %tr\r\n      %td\r\n        %p\r\n          .left_text м. Львів\r\n          .right_text= locale_date(contract.start_date, \"%d %B %Y\", :ua)\r\n      %td\r\n        %p\r\n          .left_text Lviv\r\n          .right_text= locale_date(contract.start_date, \"%B %d, %Y\", :en)\r\n    %tr\r\n      %td.padding_top_11\r\n        %p\r\n          %span.bold ЗАМОВНИК:\r\n          Підприємство\r\n          %span.bold JetThoughts LLC,\r\n          в особі директора\r\n          %span.bold Pavlo Nikitochkin\r\n          з одного боку, та\r\n          %span.bold ВИКОНАВЕЦЬ:\r\n          \#{person_type_for_locale(person_type, :ua)}\r\n          %span.bold= \"\#{ua_user_field.full_name},\"\r\n          - if person_type == \"PE\"\r\n            який діє на підставі Свідоцтва про державну реєстрацію суб’єкта підприємницької діяльності\r\n            No \#{user.register_number} та є платником єдиного податку, з іншого боку, уклали цей ДОГОВІР про\r\n            наступне:\r\n          - else\r\n            з іншого боку, уклали цей ДОГОВІР про наступне:\r\n      %td.padding_top_11\r\n        %p\r\n          %span.bold The COMPANY: JetThoughts LLC\r\n          in the person of its Director\r\n          %span.bold Pavlo Nikitochkin\r\n          on one part, and\r\n          %span.bold The CONTRACTOR:\r\n          \#{person_type_for_locale(person_type, :en)}\r\n          %span.bold= \"\#{en_user_field.full_name},\"\r\n          - if person_type == \"PE\"\r\n            acting on the basis of the Certificate on State registration of a subject of entrepreneurial\r\n            activity # \#{user.register_number} and is the taxpayer of the single tax on the other part have\r\n            concluded the AGREEMENT on the following:\r\n          - else\r\n            on the other part have concluded the AGREEMENT on the following:\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 1. ТЕРМІНИ ДОГОВОРУ\r\n        %p 1.1. У даному ДОГОВОРІ терміни вживаються у такому значенні:\r\n        %br\r\n        %p\r\n          %span.bold ДОГОВІР\r\n          – це даний документ, який включає в себе всі додатки до нього та всю зазначену в ньому\r\n          документацію, а також будь-які зміни та/або доповнення, які СТОРОНИ можуть укласти в майбутньому в\r\n          письмовій формі, підписані уповноваженими представниками СТОРІН, які встановлюють, доповнюють,\r\n          конкретизують, уточнюють або змінюють умови ДОГОВОРУ або доповнень до нього.\r\n        %br\r\n        %p\r\n          %span.bold ЗАМOВНИК\r\n          – особа, що надає ВИКОНАВЦЕВІ ЗАМОВЛЕННЯ, приймає його та сплачує ВИНАГОРОДУ  на умовах ДОГОВОРУ.\r\n          Під терміном ЗАМОВНИК розуміється ”JetThoughts LLC”.\r\n        %br\r\n        %p\r\n          %span.bold КЛІЄНТ ЗАМОВНИКА\r\n          – фізична або юридична особа, в інтересах якої може виконуватися ЗАМОВЛЕННЯ.\r\n        %br\r\n        %p\r\n          %span.bold ВИКОНАВЕЦЬ\r\n          – особа, яка приймає ЗАМОВЛЕННЯ, якісно його виконує та передає ЗАМОВНИКОВІ на умовах ДОГОВОРУ.\r\n        %br\r\n        %p\r\n          %span.bold ЗАМОВЛЕННЯ\r\n          – завдання, надане ЗАМОВНИКОМ ВИКОНАВЦЕВІ на умовах ДОГОВОРУ та ТЕХНІЧНОГО ЗАВДАННЯ.\r\n        %br\r\n        %p\r\n          %span.bold СТОРОНИ\r\n          – ЗАМОВНИК та ВИКОНАВЕЦЬ по ДОГОВОРУ при зазначенні їх разом по тексту одночасно.\r\n        %br\r\n        %p\r\n          %span.bold ВИНАГОРОДА\r\n          – плата ЗАМОВНИКОМ ВИКОНАВЦЕВІ грошової суми за виконання останнім ЗАВДАННЯ згідно умов ДОГОВОРУ.\r\n        %br\r\n        %p\r\n          %span.bold ТЕХНІЧНЕ ЗАВДАННЯ\r\n          – Додаток A до цього ДОГОВОРУ, який містить в собі ЗАМОВЛЕННЯ (декілька ЗАМОВЛЕНЬ) ЗАМОВНИКА\r\n          ВИКОНАВЦЕВІ, в якому обумовлені термін виконання; основні цілі та характеристики ЗАМОВЛЕННЯ;\r\n          розмір ВИНАГОРОДИ; ім’я або найменування КЛІЄНТА ЗАМОВНИКА, якщо ЗАМОВЛЕННЯ виконується в\r\n          інтересах КЛІЄНТА ЗАМОВНИКА; та інші вимоги, погоджені СТОРОНАМИ і що складає невід’ємну частину\r\n          цього ДОГОВОРУ.\r\n      %td\r\n        %p.bold.padding_top_11 1. DEFINITION OF TERMS\r\n        %p 1.1. Terms of the AGREEMENT shall have the following meaning:\r\n        %br\r\n        %p\r\n          %span.bold AGREEMENT\r\n          shall mean the present document which includes its all appendices and all documentation stated as\r\n          well as any amendments and/or addendum that the PARTIES can conclude in the future in writing and\r\n          signed by authorized representatives of the PARTIES which set up, annex, concretize, specify or\r\n          amend the terms of the AGREEMENT or its addendum.\r\n        %br\r\n        %p\r\n          %span.bold COMPANY\r\n          shall mean a person who provides the CONTRACTOR with the ORDER, accepts it and pays the\r\n          REMUNERATION under the AGREEMENT. The term COMPANY shall mean “JetThoughts LLC”.\r\n        %br\r\n        %p\r\n          %span.bold COMPANY СLIENT\r\n          shall mean a person or a legal entity for the benefit of whom the ORDER can be fulfilled.\r\n        %br\r\n        %p\r\n          %span.bold CONTRACTOR\r\n          shall mean a person who undertakes the ORDER, properly carries it out and transfers it to the\r\n          COMPANY under the AGREEMENT.\r\n        %br\r\n        %p\r\n          %span.bold ORDER\r\n          shall mean an assignment given to the CONTRACTOR by the COMPANY under the AGREEMENT and the\r\n          SPECIFICATION OF REQUIREMENTS .\r\n        %br\r\n        %p\r\n          %span.bold PARTIES\r\n          shall mean the COMPANY and the CONTRACTOR under the AGREEMENT when mentioned in the text\r\n          altogether.\r\n        %br\r\n        %p\r\n          %span.bold REMUNERATION\r\n          shall mean payment of the sum of money made by the COMPANY to the CONTRACTOR under the AGREEMENT.\r\n        %br\r\n        %p\r\n          %span.bold SPECIFICATION OF REQUIREMENTS\r\n          shall mean an Appendix A attached hereto, containing ORDER (several ORDERS) of the COMPANY to the\r\n          CONTRACTOR and which stipulates the term of fulfillment, principal goals and characteristics of\r\n          the ORDER; amount of the REMUNERATION; name of the COMPANY CLIENT, if the ORDER is fulfilled for\r\n          the benefit of the COMPANY CLIENT and other requirements agreed upon by the PARTIES and which\r\n          compose an integral part of the AGREEMENT.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 2. ПРЕДМЕТ ДОГОВОРУ\r\n        %p\r\n          2.1. ЗАМОВНИК доручає, приймає та оплачує виконане ЗАМОВЛЕННЯ, а ВИКОНАВЕЦЬ бере на себе\r\n          виконання та здачу ЗАМОВЛЕННЯ, зміст і обсяг якого в цілому та по етапах визначається СТОРОНАМИ в\r\n          ТЕХНІЧНОМУ ЗАВДАННІ.\r\n        %p\r\n          2.2. ЗАМОВЛЕННЯ виконується ВИКОНАВЦЕМ у відповідності до узгодженого СТОРОНАМИ ТЕХНІЧНОГО\r\n          ЗАВДАННЯ, а також загальних технічних, наукових й інших вимог.\r\n        %p\r\n          2.3. Термін виконання окремих етапів (розділів, частин) ЗАМОВЛЕННЯ визначається ТЕХНІЧНИМ\r\n          ЗАВДАННЯМ. У випадку дострокового виконання ЗАМОВЛЕННЯ ВИКОНАВЦЕМ СТОРОНИ окремо визначають порядок\r\n          приймання та оплати виконаного ЗАМОВЛЕННЯ.\r\n      %td\r\n        %p.bold.padding_top_11 2. THE SUBJECT OF THE AGREEMENT\r\n        %p\r\n          2.1. The COMPANY shall entrust with, accept and pay for the ORDER and the CONTRACTOR shall\r\n          undertake the fulfillment and delivery of the ORDER structure and full scope of which shall be\r\n          defined by the PARTIES in the SPECIFICATION OF REQUIREMENTS.\r\n        %p\r\n          2.2. The ORDER shall be fulfilled by the CONTRACTOR according to the SPECIFICATION OF\r\n          REQUIREMENTS approved by the PARTIES as well as general technical, scientific and other\r\n          requirements.\r\n        %p\r\n          2.3. Terms of fulfillment of separate stages (categories, parts) of the ORDER shall be specified\r\n          by the SPECIFICATION OF REQUIREMENTS. In case of early fulfillment of the ORDER by the CONTRACTOR\r\n          the PARTIES shall define an individual procedure of acceptance and payment for the ORDER fulfilled.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 3. ПОРЯДОК СПЛАТИ ВИНАГОРОДИ\r\n        %p\r\n          3.1. За виконання ЗАМОВЛЕННЯ на умовах ДОГОВОРУ, ЗАМОВНИК сплачує ВИКОНАВЦЕВІ ВИНАГОРОДУ,\r\n          загальний розмір якої визначається в ТЕХНІЧНОМУ ЗАВДАННІ по кожному конкретному ЗАМОВЛЕННЮ та\r\n          включається до щомісячного (160 годин) інвойсу, створеного ВИКОНАВЦЕМ.\r\n        %p\r\n          3.2. ЗАМОВНИК здійснює сплату ВИНАГОРОДИ ВИКОНАВЦЕВІ протягом 14 (чотирнадцяти) календарних днів з\r\n          дати надсилання інвойсу. За домовленістю сторін ЗАМОВНИК може здійснювати передоплату ВИКОНАВЦЕВІ.\r\n          %br\r\n          З огляду на строки завершення надання окремих обсягів (етапів) ЗАМОВЛЕННЯ. СТОРОНИ можуть\r\n          встановлювати інший порядок сплати ВИНАГОРОДИ, особливості якої визначаються за взаємною\r\n          домовленістю в письмовій формі.\r\n        %p\r\n          3.3. СТОРОНИ ДОГОВОРУ домовились про такий порядок сплати ВИНАГОРОДИ:\r\n          %br\r\n          Сплата ВИНАГОРОДИ за надані послуги згідно умов ДОГОВОРУ здійснюється у безготівковому порядку,\r\n          шляхом перерахування ЗАМОВНИКОМ належної до сплати суми, на поточний банківський рахунок\r\n          ВИКОНАВЦЯ, якщо інше письмово не погоджено СТОРОНАМИ.\r\n      %td\r\n        %p.bold.padding_top_11 3. THE PROCEDURE OF PAYMENT OF THE REMUNERATION\r\n        %p\r\n          3.1. The COMPANY shall pay to the CONTRACTOR the REMUNERATION for the fulfillment of the ORDER,\r\n          total amount of which shall be stipulated by the SPECIFICATION OF REQUIREMENTS for each particular\r\n          ORDER and included in the monthly (160 hours) invoices, sent by the CONTRACTOR.\r\n        %p\r\n          3.2. The COMPANY shall pay the REMUNERATION during the period of 14 (fourteen) calendar days from\r\n          the date of the invoice. By the mutual AGREEMENT of the Parties the COMPANY may do a prepayment to\r\n          the CONTRACTOR.\r\n          %br\r\n          Taking into consideration the terms of completing of provision of certain parts (phases) of the\r\n          ORDER, the PARTIES can establish another procedure of payment of the REMUNERATION, specific\r\n          details of which shall be stipulated upon mutual AGREEMENT of the PARTIES in written form.\r\n        %p\r\n          3.3. The Parties have agreed on the following procedure of payment of the REMUNERATION:\r\n          %br\r\n          Payment of the REMUNERATION for the rendered services by the CONTRACTOR under the AGREEMENT shall\r\n          be carried out as a money transfer by the COMPANY of the due and necessary sum to the current bank\r\n          account of the CONTRACTOR unless otherwise has been agreed by the PARTIES in written form.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 4. ОСОБЛИВОСТІ ВИКОНАННЯ ТА ПРИЙМАННЯ ЗАМОВЛЕННЯ\r\n        %p 4.1. Виконання ЗАМОВЛЕННЯ:\r\n        %p\r\n          4.1.1. ВИКОНАВЕЦЬ виконує свої обов’язки за цим ДОГОВОРОМ особисто. Наймані працівники ВИКОНАВЦЯ\r\n          або треті особи можуть залучатись ВИКОНАВЦЕМ до виконання ЗАМОВЛЕНЬ за ДОГОВОРОМ лише за письмовим\r\n          погодженням із ЗАМОВНИКОМ.\r\n        %p\r\n          4.1.2. ЗАМОВНИК не має право обмежувати ВИКОНАВЦЯ при здійсненні останнім його підприємницької\r\n          діяльності. ЗАМОВНИК не заперечує про підприємницькі стосунки ВИКОНАВЦЯ з третіми особами та\r\n          конкурентами ЗАМОВНИКА.\r\n        %p 4.1.3. МІСЦЕМ ВИКОНАННЯ ЗАМОВЛЕННЯ є переважно територія ЗАМОВНИКА (офісні чи інші приміщення).\r\n        %p\r\n          4.1.4. Якщо МІСЦЕ ВИКОНАННЯ ЗАМОВЛЕННЯ знаходиться на території ЗАМОВНИКА, без його згоди\r\n          ВИКОНАВЕЦЬ не може виносити обладнання, устаткування чи інше майно, документи, інформацію на\r\n          будь-яких носіях, що є власністю ЗАМОВНИКА, або власністю третіх осіб, що знаходится у\r\n          розпорядженні ЗАМОВНИКА.\r\n        %p\r\n          4.1.5. ЗАМОВНИК має право щоденно, якщо це не перешкоджає роботі, перевіряти хід виконання\r\n          ЗАМОВЛЕННЯ за ДОГОВОРОМ.\r\n        %p 4.2. Конкуренція\r\n        %p\r\n          4.2.1. ВИКОНАВЕЦЬ, з метою захисту конфіденційної та комерційної інформації про господарську\r\n          діяльність ЗАМОВНИКА, без згоди ЗАМОВНИКА, зобов’язується не мати безпосередні або опосередковані\r\n          (через третіх осіб) контакти з будь-якими клієнтами ЗАМОВНИКА (у т. ч. потенційними - юридичними\r\n          або фізичними особами - підприємцями, які займаються діяльністю, подібною до діяльності КЛІЄНТІВ\r\n          ЗАМОВНИКА), якщо такі контакти завдають або можуть завдати шкоди фінансовим або іншим інтересам\r\n          ЗАМОВНИКА, зменшити очікувані ним прибутки.\r\n        %p\r\n          4.2.2. ВИКОНАВЕЦЬ зобов’язується:\r\n          %br\r\n          а) без згоди ЗАМОВНИКА не рекомендувати клієнтам ЗАМОВНИКА будь-яких третіх осіб (фізичних та/або\r\n          юридичних), які виконують роботи та/або надають послуги, аналогічні роботам чи послугам, що\r\n          виконуються/надаються або можуть виконуватися/надаватися ЗАМОВНИКОМ за ДОГОВОРОМ.\r\n          %br\r\n          б) ніяким чином не сприяти зверненню клієнтів ЗАМОВНИКА до будь-яких третіх осіб (фізичних та/або\r\n          юридичних, які володіють будь-якою часткою власності, або є акціонерами у компаніях клієнтів\r\n          ЗАМОВНИКА) для отримання аналогічних робіт та/або послуг, що виконуються/надаються ЗАМОВНИКОМ за\r\n          ДОГОВОРОМ.\r\n          %br\r\n          в) не вести переговори з будь-якими клієнтами ЗАМОВНИКА стосовно рекомендації послуг третіх осіб\r\n          або будь-якого іншого співробітництва з ними,  якщо це не було попередньо схвалено ЗАМОВНИКОМ.\r\n          %br\r\n          г) протягом 1-го року після завершення правовідносин за ДОГОВОРОМ не виконувати роботи,\r\n          передбачені Технічним Завданням для конкурентів КЛІЄНТІВ ЗАМОВНИКА (фізичні та/або юридичні особи,\r\n          які займаються подібними видами діяльності).\r\n      %td\r\n        %p.bold.padding_top_11 4. SPECIFIC FEATURES OF FULFILLMENT OF THE ORDER\r\n        %p 4.1. Fulfillment of the ORDER:\r\n        %p\r\n          4.1.1. The CONTRACTOR shall personally fulfill its obligations hereunder. The employees of the\r\n          CONTRACTOR or any third persons can be engaged by the CONTRACTOR into fulfilling of the ORDER under\r\n          the AGREEMENT upon written or verbal consent of the COMPANY.\r\n        %p\r\n          4.1.2. The COMPANY is not entitled to limit the CONTRACTOR’s business activity. The COMPANY shall\r\n          not object to the CONTRACTOR’s business activities with third parties and COMPANY’s competitors.\r\n        %p\r\n          4.1.3. PLACE OF THE ORDER FULFILLMENT is predominantly the territory of the CONTRACTOR (offices\r\n          and other premises).\r\n        %p\r\n          4.1.4. Should the PLACE OF THE ORDER FULFILLMENT be in the territory of the COMPANY, the\r\n          CONTRACTOR shall have no right without consent of the COMPANY to take out any equipment or other\r\n          property, documents, information on any storage media, which are owned by the COMPANY or by other\r\n          third persons and are at the COMPANY’s disposal.\r\n        %p\r\n          4.1.5. The COMPANY shall have the right to check daily the process of the ORDER fulfillment\r\n          hereunder, provided this doesn’t interfere with ORDER fulfillment.\r\n        %p 4.2. Competition\r\n        %p\r\n          4.2.1. The CONTRACTOR with the aim of protection of confidential and commercial information on\r\n          the economic activity of the COMPANY shall not without consent of the COMPANY have direct or\r\n          indirect (via third persons) contacts with any clients of the COMPANY (including potential ones,\r\n          that is legal entities or natural persons that undertake the activity similar to that of the\r\n          COMPANY) in case such contacts cause or can cause financial losses or damage other interests of the\r\n          COMPANY and reduce its expected profits.\r\n        %p\r\n          4.2.2. The CONTRACTOR shall:\r\n          %br\r\n          a) without consent of the COMPANY recommend to no clients of the COMPANY any third persons\r\n          (natural persons and/or legal entities) that carry out works and/or provide services similar to\r\n          the works and services which are carried out/provided or can be carried out/provided by the\r\n          COMPANY hereunder.\r\n          %br\r\n          b) in no way assist the clients of the COMPANY in contacting any third persons (private persons or\r\n          legal entities that possess any portion of ownership or shareholding in the companies of COMPANY\r\n          clients) with the aim of receiving similar works and /or services which are carried out/provided\r\n          by the COMPANY hereunder.\r\n          %br\r\n          c) carry out no negotiations with any clients of the COMPANY as to the recommendation of services\r\n          of third parties or any other form of cooperation with them unless it has been prior agreed upon\r\n          by the COMPANY.\r\n          %br\r\n          d) during the 1st year after completing the legal relations under the AGREEMENT carry out no works\r\n          which represent the subject of the present AGREEMENT for the competitors of the COMPANY CLIENTS\r\n          (natural persons and/or legal entities engaged in the similar kinds of activity).\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 5. ОБОВ’ЯЗКИ СТОРІН\r\n        %p 5.1. Обов’язки ЗАМОВНИКА:\r\n        %p\r\n          5.1.1. Забезпечити ВИКОНАВЦЯ інформацією, матеріалами та обладнанням, необхідними для виконання\r\n          ЗАМОВЛЕННЯ на умовах ДОГОВОРУ та прийняти належним чином виконане ВИКОНАВЦЕМ ЗАМОВЛЕННЯ в порядку,\r\n          встановленому ДОГОВОРОМ.\r\n        %p 5.1.2. на вимогу ВИКОНАВЦЯ, надати рекомендації щодо наданих за даним ДОГОВОРОМ послуг.\r\n        %p\r\n          5.1.3. Сплатити ВИНАГОРОДУ за належним чином виконане ВИКОНАВЦЕМ ЗАМОВЛЕННЯ в розмірі,\r\n          визначеному у відповідному ТЕХНІЧНОМУ ЗАВДАННІ  (або іншим узгодженим СТОРОНАМИ документом).\r\n          Прийняттю та оплаті підлягають лише ті ЗАМОВЛЕННЯ, які виконані ВИКОНАВЦЕМ належним чином та в\r\n          повному обсязі у відповідності до ТЕХНІЧНОГО ЗАВДАННЯ.\r\n        %p 5.2. Обов’язки ВИКОНАВЦЯ:\r\n        %p\r\n          5.2.1. Виконувати ЗАМОВЛЕННЯ належним чином, у повному обсязі, з належною якістю та у суворій\r\n          відповідності до ТЕХНІЧНОГО ЗАВДАННЯ та передавати останньому його у визначений строк.\r\n        %p\r\n          5.2.2. Не передавати будь-яким третім особам, не розголошувати та не оприлюднювати в будь-який\r\n          інший спосіб без попередньої письмової згоди ЗАМОВНИКА одержані результати ЗАМОВЛЕННЯ за ДОГОВОРОМ,\r\n          відомості про такі результати або пов’язані з ними.\r\n        %p\r\n          5.2.3. Не використовувати в комерційних або інших цілях без попереднього письмового дозволу\r\n          ЗАМОВНИКА або клієнтів ЗАМОВНИКА, інформацію, отриману в ході виконання ДОГОВОРУ.\r\n        %p 5.2.4. На вимогу ЗАМОВНИКА інформувати його про хід виконання ДОГОВОРУ.\r\n        %p\r\n          5.2.5. Ефективно та обережно використовувати обчислювальну та інші техніку (обладнання, меблі\r\n          тощо), надану ЗАМОВНИКОМ для виконання ДОГОВОРУ, ощадливо та раціонально витрачати канцелярські\r\n          товари та інші матеріали, електроенергію, а також інші матеріальні ресурси. ВИКОНАВЕЦЬ без згоди\r\n          ЗАМОВНИКА не має права володіти, користуватися чи розпоряджатися будь-яким майном останнього.\r\n        %p\r\n          5.2.6. Не встановлювати на обладнання ЗАМОВНИКА будь-яке програмне забезпечення без попередньої\r\n          письмової згоди останнього; не використовувати надане обладнання для розробки комп’ютерних\r\n          програм, що мають ознаки вірусів, розповсюджувати та/або зберігати матеріали, у будь-якій формі,\r\n          що суперечать засадам суспільної моралі (наприклад, матеріали з насильством або порнографією).\r\n          %br\r\n          ВИКОНАВЕЦЬ має право встановлювати та використовувати на обладнанні ЗАМОВНИКА таке програмне\r\n          забезпечення, що відповідає ліцензійним вимогам про безкоштовне або умовно-безкоштовне програмне\r\n          забезпечення. Опис даних видів програмного забезпечення наведено нижче.\r\n          %br\r\n          Безкоштовне програмне забезпечення – це захищене авторським правом програмне забезпечення, плата\r\n          за користування яким відсутня.\r\n          %br\r\n          Умовно-безкоштовне програмне забезпечення - це захищене авторським правом програмне забезпечення,\r\n          плата за користування яким відсутня тільки впродовж певного випробувального терміну.\r\n        %p\r\n          5.2.7. Відшкодувати ЗАМОВНИКУ збитки, якщо вони завдані з вини  ВИКОНАВЦЯ, будь-яким пошкодженням\r\n          чи втратою майна в повному обсязі.\r\n        %p\r\n          5.2.8. По завершенню договірних відносин повернути ЗАМОВНИКУ майно, документацію, та інші\r\n          технічні засоби, які були ним передані для виконання робіт за ДОГОВОРОМ.\r\n        %br\r\n      %td\r\n        %p.bold.padding_top_11 5. OBLIGATIONS OF THE PARTIES\r\n        %p 5.1. The COMPANY shall:\r\n        %p\r\n          5.1.1. provide the CONTRACTOR with information, materials necessary to fulfill the ORDER under\r\n          the AGREEMENT and accept the ORDER properly fulfilled by the CONTRACTOR according to the procedure\r\n          stipulated by the AGREEMENT.\r\n        %p\r\n          5.1.2. provide reference letters on CONTRACTOR’S demand as to services provided under the scope\r\n          of this contract.\r\n        %p\r\n          5.1.3. pay out the REMUNERATION for the ORDER properly fulfilled by the CONTRACTOR in the amount\r\n          stipulated in the SPECIFICATION OF REQUIREMENTS (or other document agreed by the PARTIES). Only\r\n          ORDERS properly and completely fulfilled by the CONTRACTOR according to SPECIFICATION OF\r\n          REQUIREMENTS shall be liable to payment and acceptance.\r\n        %p 5.2. The CONTRACTOR shall:\r\n        %p\r\n          5.2.1. fulfill the ORDER properly, completely, with required quality and in compliance with the\r\n          SPECIFICATION OF REQUIREMENTS and transfer to the COMPANY within the stipulated terms.\r\n        %p\r\n          5.2.2. not transfer the ORDER to any third persons, shall not disclose and publish in any other\r\n          way the results of the ORDER fulfilled under the AGREEMENT as well as the information on such\r\n          results or related thereto without prior written consent of the COMPANY.\r\n        %p\r\n          5.2.3. not use information received in the course of the fulfillment of the AGREEMENT in\r\n          commercial or other purposes without written consent of the COMPANY or clients of the COMPANY.\r\n        %p 5.2.4. inform the COMPANY on the process of implementation of the AGREEMENT at its request.\r\n        %p\r\n          5.2.5. effectively and carefully use computing and other devices (equipment, furniture etc.)\r\n          provided by the COMPANY for the fulfillment of the AGREEMENT, wisely and rationally use stationary\r\n          and other materials, electric energy as well as other material resources. The CONTRACTOR shall not\r\n          have the ownership right, the right to use and dispose of any property of the COMPANY without\r\n          consent of the latter.\r\n        %p\r\n          5.2.6. Do not install any software at the equipment of the COMPANY without prior written consent\r\n          of the latter; shall not use the equipment provided for the development of computer programs which\r\n          has signs of viruses and distribute and/or store materials in any form that contradict moral\r\n          standards of the society (e.g. materials containing violence or pornography).\r\n          %br\r\n          The CONTRACTOR shall have the right to install and use software at the equipment of the COMPANY\r\n          under the license requirements to the free or shareware software. The description of the software\r\n          is provided below.\r\n          %br\r\n          Free software – software protected by the copyright used on a free-of-charge basis.\r\n          %br\r\n          Shareware software – software protected by the copyright used on a free-of-charge basis only\r\n          during a definite trial period.\r\n        %p\r\n          5.2.7. compensate the damages to the COMPANY, which were inflicted by the CONTRACTOR by any\r\n          damage or loss of the property in full.\r\n        %p\r\n          5.2.8. return property, documentation and other technical facilities transferred with the aim of\r\n          work fulfillment under the AGREEMENT to the COMPANY after the expiration of the contract relations.\r\n        %br\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 6. ВІДПОВІДАЛЬНІСТЬ СТОРІН\r\n        %p\r\n          6.1. За порушення взятих за ДОГОВОРОМ зобов’язань СТОРОНИ несуть відповідальність, передбачену\r\n          чинним законодавством України та ДОГОВОРОМ.\r\n      %td\r\n        %p.bold.padding_top_11 6. RESPONSIBILITIES OF THE PARTIES\r\n        %p\r\n          6.1. The PARTIES shall bear the responsibility stipulated by the current legislation of Ukraine\r\n          and the AGREEMENT for the breach of the obligations under the AGREEMENT.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 7. ЗБЕРЕЖЕННЯ КОМЕРЦІЙНОЇ ТАЄМНИЦІ ТА КОНФІДЕНЦІЙНОЇ ІНФОРМАЦІЇ\r\n        %p\r\n          7.1. Сторони домовились, що текст ДОГОВОРУ (крім випадків, передбачених законодавством України),\r\n          вихідні данні та виконані ЗАМОВЛЕННЯ, інформація, документація, що передані або створені\r\n          ВИКОНАВЦЕМ при виконанні ДОГОВОРУ, виражені формі: документ, інформація та ін. є конфіденційними\r\n          та складають комерційну таємницю ЗАМОВНИКА та за будь-яких умов не підлягає розголошенню\r\n          ВИКОНАВЦЕМ.\r\n          %br\r\n          Також не підлягає розголошенню, третім особам, інформація щодо ВИКОНАВЦЯ, яка не стосується\r\n          виконання цього ДОГОВОРУ.\r\n        %p\r\n          7.2. Протягом всього терміну дії ДОГОВОРУ ВИКОНАВЕЦЬ не буде розголошувати ніякої інформації,\r\n          отриманої від ЗАМОВНИКА, що містить комерційну таємницю або є конфіденційною, а також щодо\r\n          переліченого у п. п. 7.1. ДОГОВОРУ, будь-якій іншій особі, підприємству, організації тощо та не\r\n          буде використовувати цю інформацію для своєї власної вигоди.\r\n        %p\r\n          7.3. Інформація не буде вважатися конфіденційною та ВИКОНАВЕЦЬ не буде мати зобов’язань стосовно\r\n          такої інформації, якщо вона відповідає хоча б одній із таких умов:\r\n          %br\r\n          а) вже була відома ВИКОНАВЦЮ до укладання ДОГОВОРУ;\r\n          %br\r\n          б) отримана легально від третьої особи поза зв’язком з цим ДОГОВОРОМ;\r\n          %br\r\n          в) розкрита публічно самим ЗАМОВНИКОМ;\r\n          %br\r\n          г) розкрита за обґрунтованою письмовою вимогою державного органу, повноваження якого щодо отримання такої інформації, визначені чинним законодавством України;\r\n          %br\r\n          ґ) дозволена до розповсюдження з письмової  згоди ЗАМОВНИКА.\r\n          %br\r\n          У кожному зазначеному випадку ВИКОНАВЕЦЬ повинен надати ЗАМОВНИКУ письмові пояснення по кожному випадку оприлюднення чи витоку інформації з документальним підтвердженням правомірності своїх дій протягом 10 календарних днів з дати надходження письмового запиту.\r\n        %p\r\n          7.4. ВИКОНАВЕЦЬ гарантує збереження конфіденційності особисто, а також стосовно своїх найманих\r\n          працівників. ВИКОНАВЕЦЬ зобов'язується застосувати всіх необхідних заходів для того, щоб запобігти\r\n          повному або частковому розголошенню будь-яким способом комерційної таємниці. Відомості, що\r\n          складають комерційну таємницю в будь-якій формі, можуть бути передані третім особам винятково за\r\n          попередньою письмовою згодою ЗАМОВНИКА.\r\n        %p\r\n          7.5. У випадку залучення ВИКОНАВЦЕМ до виконання робіт найманих працівників або третіх осіб, він\r\n          гарантує, що з переданою документацією та інформацією будуть ознайомлені тільки ті особи, що\r\n          безпосередньо пов'язані із виконанням ЗАМОВЛЕННЯ за ДОГОВОРОМ. Перелік таких осіб погоджується з\r\n          ЗАМОВНИКОМ у письмовій формі. Крім того, з цими працівниками обов’язково укладаються угоди про\r\n          нерозголошення комерційної інформації та умов ДОГОВОРУ. Форма (зразок) такої угоди надається\r\n          ЗАМОВНИКУ на затвердження.\r\n        %p\r\n          7.6. У випадку розголошення конфіденційної інформації або комерційної таємниці за ДОГОВОРОМ\r\n          ВИКОНАВЦЕМ, його найманими працівниками або залученими ним третіми особами, партнерами з\r\n          господарській діяльності або особами з їх персоналу, ВИКОНАВЕЦЬ відповідає відповідно до положень\r\n          чинного законодавства та зобов'язується відшкодувати ЗАМОВНИКУ всі понесені в зв'язку з цим збитки.\r\n        %p\r\n          7.7. Зобов'язання щодо зберігання конфіденційності продовжують зберігати свою силу і після\r\n          завершення дії цього ДОГОВОРУ або його дострокового розірвання протягом 5-ти років після\r\n          припинення дії (розірвання) ДОГОВОРУ.\r\n      %td\r\n        %p.bold.padding_top_11 7. KEEPING OF COMMERCIAL SECRET AND CONFIDENTIAL INFORMATION\r\n        %p\r\n          7.1. The Parties have agreed that the text of the AGREEMENT (except for cases stipulated by the\r\n          legislation of Ukraine), outgoing data and ORDERS fulfilled, information, documentation\r\n          transferred or created by the CONTRACTOR during the fulfillment of the AGREEMENT in form:\r\n          document, information etc. shall be considered confidential and constitute commercial secret of\r\n          the COMPANY and shall be subject to disclosure by the CONTRACTOR under no circumstances.\r\n          %br\r\n          Personal information about CONTRACTOR is also agreed to be confidential and COMPANY shall not\r\n          disclose that to third parties.\r\n        %p\r\n          7.2. During the whole period of validity of the AGREEMENT the CONTRACTOR shall not disclose any\r\n          information obtained from the COMPANY which contains commercial secret or is confidential as well\r\n          as disclose the information listed in paragraph 7.1 to any person, enterprise or organization etc.\r\n          and use this information for its own benefit.\r\n        %p\r\n          7.3. Information shall not be considered confidential and the CONTRACTOR shall not have any\r\n          obligations as to such information in case it meets at least one of the following requirements:\r\n          %br\r\n          a) was already known to the CONTRACTOR before concluding the AGREEMENT;\r\n          %br\r\n          b) was legally obtained from a third person not in connection with the AGREEMENT;\r\n          %br\r\n          c) was publicly disclosed by the COMPANY itself;\r\n          %br\r\n          d) was disclosed due to the substantiated written request of the State authority responsibilities\r\n          of which include obtaining of such information stipulated by the current legislation of Ukraine;\r\n          %br\r\n          e) was allowed to distribution according to the written consent of the COMPANY.\r\n          %br\r\n          The CONTRACTOR in any case shall provide the COMPANY with the written explanations concerning each\r\n          case of disclosure or information leakage as well as confirm with the documents the legitimacy of\r\n          its actions within 10 calendar days from the date of receiving of the written inquiry.\r\n        %p\r\n          7.4. The CONTRACTOR shall guarantee the preservation of confidentiality personally and on behalf\r\n          of its employees. The CONTRACTOR shall use all necessary measures to prevent full or partial\r\n          disclosure of the commercial secret. Information constituting commercial secret in any form can be\r\n          transferred to third persons only upon the prior written consent of the COMPANY.\r\n        %p\r\n          7.5. Should the CONTRACTOR engage hired employees or third persons into fulfillment of works, he\r\n          shall guarantee that only those persons directly related to the fulfillment of the ORDER under the\r\n          AGREEMENT will be familiarized with documentation and information transferred. The list of such\r\n          persons shall be agreed with the COMPANY in writing. Moreover, such persons shall sign AGREEMENTS\r\n          on non-disclosure of the commercial information and terms of the AGREEMENT. The COMPANY shall be\r\n          provided with the form (draft) of such AGREEMENT for approval.\r\n        %p\r\n          7.6. Should the confidential information or commercial secret under the AGREEMENT be disclosed by\r\n          the CONTRACTOR, its employees or third persons engaged by him, partners in economic activities or\r\n          their personnel, the CONTRACTOR shall bear responsibility according to the current legislation and\r\n          shall compensate to the COMPANY the damages inflicted in connection with it.\r\n        %p\r\n          7.7. Liabilities as to the preservation of confidentiality shall remain in force after the\r\n          expiration of validity of the AGREEMENT or its early termination for over 5 years from the date of\r\n          the expiration (termination) of the AGREEMENT.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 8. ФОРС-МАЖОРНІ ОБСТАВИНИ\r\n        %p\r\n          8.1 СТОРОНИ повинні неухильно виконувати свої зобов’язання за ДОГОВОРОМ. Єдиною підставою для\r\n          звільнення від відповідальності визнаються обставини, викликані подіями, що не залежать від волі\r\n          сторони, яка не могла б їх уникнути, якщо ці обставини наступили після укладання ДОГОВОРУ та\r\n          заважають його повному або частковому виконанню (форс-мажор або обставини непереборноїсили).\r\n        %p\r\n          8.2. Форс-мажорними обставинами за ДОГОВОРОМ є: пожежа, стихійне лихо, зміна законодавства\r\n          України, дії органів державної влади України або інші подібні обставини, що не залежать від волі\r\n          СТОРІН.\r\n        %p\r\n          8.3. Про настання форс-мажорних обставин, передбачуваний термін їх дії та припинення СТОРОНА, для\r\n          якої вони настали, письмово сповіщає іншу СТОРОНУ. Неповідомлення про настання форс-мажорних\r\n          обставин позбавляє СТОРОНУ права посилатися на них, як на підстави звільнення від відповідальності.\r\n        %p\r\n          8.4. Настання форс-мажорних обставин і тривалість їхньої дії, підтверджуються довідками\r\n          Торгово-промислових палат за місцем їх настання або іншими документами компетентних органів.\r\n        %p\r\n          8.5. Якщо форс-мажорні обставини тривають більш шести місяців, кожна зі СТОРІН вправі розірвати\r\n          ДОГОВІР, повернувши іншій Стороні все майно чи грошові кошти, що були наперед отримані для\r\n          виконання цього ДОГОВОРУ до кінця строку його дії. При цьому жодна із СТОРІН не має права вимагати\r\n          від іншої відшкодування можливих збитків.\r\n      %td\r\n        %p.bold.padding_top_11 8. FORCE MAJEURE\r\n\r\n        %p\r\n          8.1. The PARTIES shall steadily carry out their obligations under the AGREEMENT. The only ground\r\n          for releasing from responsibility shall be the circumstances caused by conditions beyond the\r\n          control of the Party which could not avoid them when such circumstances appeared after concluding\r\n          of the AGREEMENT and interfere with its full or partial fulfillment (force majeure or\r\n          circumstances of insuperable force).\r\n        %p\r\n          8.2. The AGREEMENT defines the force majeure circumstances as fires, natural disasters, changes\r\n          introduced into the legislation of Ukraine, actions of the State authorities of Ukraine or other\r\n          similar circumstances beyond control of the PARTIES.\r\n        %p\r\n          8.3. The PARTY under the influence of force majeure circumstances shall notify the other PARTY in\r\n          writing on their commencement, anticipated duration and termination. Should the PARTY fail to\r\n          inform the other PARTY on the force majeure circumstances arisen, it shall be deprived of the\r\n          right to refer to them as the ground for releasing from responsibility.\r\n        %p\r\n          8.4. Arising of the force-majeure circumstances as well as their duration shall be confirmed by\r\n          the notes issued by the Chamber of Commerce and Industry at the place of the arising or any other\r\n          documents issued by competent authorities.\r\n        %p\r\n          8.5. Should the force-majeure circumstances last more than six months, each PARTY shall have the\r\n          right to terminate the AGREEMENT and return to the other PARTY all the property or monetary assets\r\n          received in advance with the aim of fulfillment of the AGREEMENT before the expiration of its\r\n          validity. Herein neither Party shall have the right to demand a compensation of damages from the\r\n          other PARTY.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 9. СТРОК ДІЇ ДОГОВОРУ\r\n        %p 9.1. Строк дії ДОГОВОРУ встановлюється до \#{locale_date(contract.end_date, \"%d %B %Y\", :ua)}.\r\n        %p\r\n          9.2. Строк дії ДОГОВОРУ може бути продовжений на наступний календарний рік, якщо жодна СТОРОНА не\r\n          виявила побажання про закінчення умов ДОГОВОРУ, повідомивши іншу СТОРОНУ за 30 календарних днів до\r\n          спливу терміну дії ДОГОВОРУ.\r\n      %td\r\n        %p.bold.padding_top_11 9. PERIOD OF VALIDITY OF THE AGREEMENT\r\n        %p 9.1. The AGREEMENT shall be valid till \#{locale_date(contract.start_date, \"%B %d, %Y\", :en)}.\r\n        %p\r\n          9.2. The validity of the AGREEMENT can be prolonged for the next calendar year should neither\r\n          PARTY wish to terminate the AGREEMENT by informing the other PARTY on this 30 days prior to the\r\n          expiration of the AGREEMENT.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 10. ІНШІ УМОВИ\r\n        %p\r\n          10.1. Після підписання ДОГОВОРУ всі попередні переговори за ним, листування, попередні угоди та\r\n          протоколи про наміри з питань, що так чи інакше стосуються ДОГОВОРУ, втрачають юридичну силу.\r\n        %p\r\n          10.2. У випадку припинення ДОГОВОРУ за будь-яких підстав, в тому числі через закінчення строку\r\n          дії, дострокове розірвання чи обставин, передбачених п.п. 8.5., ВИКОНАВЕЦЬ зобов’язаний повернути\r\n          ЗАМОВНИКУ все обладнання, матеріали, програмне забезпечення та інше майно (або грошові кошти),\r\n          отримані для виконання робіт. Електронні копії (примірники) результатів робіт (документація,\r\n          комп’ютерні програми чи їх частини), що залишаються на електронних носіях, що належать\r\n          ЗАМОВНИКОВІ, мають бути знищені.\r\n        %p\r\n          10.3. ЗАМОВНИК має право розірвати ДОГОВІР в односторонньому порядку, якщо:\r\n          %br\r\n          а) ВИКОНАВЕЦЬ порушив умови п.п. 5.2.2.; 5.2.4;\r\n          %br\r\n          б) ВИКОНАВЕЦЬ завдав збитків або порушив комерційні інтереси ЗАМОВНИКА.\r\n        %p\r\n          10.3.1. Сторони мають право розірвати ДОГОВІР в односторонньому порядку, сповістивши про це один\r\n          одного в письмовій формі за 30 календарних днів до розірвання ДОГОВОРУ. Протягом цього строку\r\n          Сторони забов’язані виконувати всі умови передбачені цим ДОГОВОРОМ.\r\n        %p\r\n          10.4. Положення пункту 7 ДОГОВОРУ зберігають свою чинність для Сторін і після закінчення дії\r\n          ДОГОВОРУ.\r\n        %p\r\n          10.5. Усі спори за ДОГОВОРОМ розв’язуються шляхом переговорів між Сторонами. У разі недосягнення\r\n          взаємної згоди, спір вирішується в судовому порядку згідно із визначеною чинним законодавством\r\n          України.\r\n      %td\r\n        %p.bold.padding_top_11 10. OTHER PROVISIONS\r\n        %p\r\n          10.1. After signing the AGREEMENT all preliminary talks, correspondence, preliminary contracts and\r\n          protocols of intentions on the issues one or other way related to the AGREEMENT shall be\r\n          considered null and void.\r\n        %p\r\n          10.2. Should the AGREEMENT be terminated due to any reasons including its expiration, early\r\n          termination or circumstances stipulated by paragraph 9.5, the CONTRACTOR shall return to the\r\n          COMPANY all the equipment, materials, software and other property (or monetary assets) obtained\r\n          for the fulfillment of works. Electronic copies (samples) of the work results (documentation,\r\n          computer programs and their parts) left on the electronic media and belong to the COMPANY shall be\r\n          destroyed.\r\n        %p\r\n          10.3. The COMPANY shall have the right to unilaterally terminate the AGREEMENT in case:\r\n          %br\r\n          a) the CONTRACTOR has violated conditions of p.p. 5.2.2.; 5.2.4.;\r\n          %br\r\n          b) the CONTRACTOR has inflicted damages or offended the commercial interests of the COMPANY.\r\n        %p\r\n          10.3.1 Parties has the right to terminate this AGREEMENT unilaterally upon notifying one another\r\n          in writing 30 calendar days prior.  During this period of 30 days parties are obligated to fulfill\r\n          all conditions stipulated in this AGREEMENT.\r\n        %p\r\n          10.4. Terms of paragraph 7 of the AGREEMENT shall remain valid for the Parties after the\r\n          expiration of the AGREEMENT.\r\n        %p\r\n          10.5. All disputes under the AGREEMENT shall be decided by negotiations between the PARTIES.\r\n          Should the PARTIES fail to reach mutual understanding the dispute shall be decided in court\r\n          according to the jurisdiction stipulated by the legislation of Ukraine.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 11. ПРИКІНЦЕВІ ПОЛОЖЕННЯ\r\n        %p 11.1. У випадках, не передбачених ДОГОВОРОМ, СТОРОНИ керуються чинним законодавством України.\r\n        %p\r\n          11.2. Усі усні застереження за ДОГОВОРОМ не мають юридичної сили. Всі зміни чи доповнення до\r\n          ДОГОВОРУ вносяться у письмовій формі та повинні бути підписані уповноваженими на це представниками\r\n          СТОРІН.\r\n        %p\r\n          11.3. ДОГОВІР складено українською (превалює) та англійською мовами у двох оригінальних,\r\n          автентичних примірниках, які мають однакову юридичну силу і зберігається у кожної із Сторін.\r\n        %p\r\n          11.4. Усі додатки до цього ДОГОВОРУ, які належним чином оформлені та підписані СТОРОНАМИ, є\r\n          невід’ємною частиною ДОГОВОРУ та підлягають виконанню СТОРОНАМИ.\r\n        %p\r\n          11.5. Даний ДОГОВІР, всі його доповнення та додатки, можуть бути підписані СТОРОНАМИ особисто із\r\n          застосуванням факсиміле, штампа, символу або іншим механічним або електронним способом\r\n          посвідчення. Копії цих документів, вислані по електронній пошті вважати дійсними та маючими повну\r\n          юридичну силу.\r\n        %p 11.6. ДОГОВІР складається з 10-и сторінок формату А4.\r\n      %td\r\n        %p.bold.padding_top_11 11. FINAL PROVISIONS\r\n        %p\r\n          11.1. In cases not stipulated by AGREEMENT the PARTIES shall be guided by the current legislation\r\n          of Ukraine.\r\n        %p\r\n          11.2. All verbal warnings as to the AGREEMENT shall have no legal force. All amendments or\r\n          appendices to the AGREEMENT shall be introduced in writing, signed by the authorized\r\n          representatives of the PARTIES and sealed.\r\n        %p\r\n          11.3. The AGREEMENT is concluded in Ukrainian (prevails) and English languages in two original and\r\n          authentic copies having equal legal force and kept by both Parties.\r\n        %p\r\n          11.4. All appendices to the AGREEMENT properly executed and signed by the PARTIES shall be\r\n          considered its integral part and are subject to fulfillment by the PARTIES.\r\n        %p\r\n          11.5. This AGREEMENT, all its Additions and Appendices may be signed by the PARTIES in person\r\n          using facsimile, stamp, symbol or any other mechanical or electronic means. Copies of these\r\n          documents sent by e-mail considered valid and with full legal force.\r\n        %p 11.6.The AGREEMENT is comprised of 10 pages of A4 format.\r\n    %tr\r\n      %td\r\n        %p.bold.padding_top_11 12. ДОДАТКИ ДО ДОГОВОРУ\r\n        %p A. Технічне завдання.\r\n        %p Б. Положення про захист інтелектуальної власності.\r\n      %td\r\n        %p.bold.padding_top_11 12. APPENDICES TO THE AGREEMENT\r\n        %p A. Specification of requirements.\r\n        %p B. The Regulation on intellectual property protection.\r\n    %tr\r\n      %td.padding_top_11\r\n        %p.bold 13. МІСЦЕЗНАХОДЖЕННЯ, БАНКІВСЬКІ РЕКВІЗИТИ ТА ПІДПИСИ СТОРІН\r\n        %br\r\n        %p.bold Замовник\r\n        %p JetThoughts LLC\r\n        %br\r\n        %p Адреса: 401 E 1st St Suite 1868 - 0039, Sanford FL , 32772\r\n        %p Тел.: +1 760 264 4264\r\n        %br\r\n        %br\r\n        %p Директор______________________/ Nikitochkin Pavlo\r\n      %td.padding_top_11\r\n        %p.bold 13. LOCATION, BANKING REQUISITES AND SIGNATURES OF THE PARTIES\r\n        %br\r\n        %p.bold The Company\r\n        %p JetThoughts LLC\r\n        %br\r\n        %p Address: 401 E 1st St Suite 1868 - 0039, Sanford FL , 32772\r\n        %p Phone: +1 760 264 4264\r\n        %br\r\n        %br\r\n        %p Director_____________________/ Nikitochkin Pavlo\r\n    %tr\r\n      %td.padding_top_11\r\n        %p.bold Виконавець\r\n        %p= \"\#{person_type_for_locale(person_type, :ua)} \#{ua_user_field.full_name}\"\r\n        %br\r\n        %p= \"Адреса: \#{ua_user_field.address}\"\r\n        %br\r\n        %p= \"Ідентифікаційний номер: \#{user.taxpayer_id}\"\r\n        %br\r\n        - if person_type == \"PE\"\r\n          %p= \"Свідоцтво № \#{user.register_number}\"\r\n          %p= \"Від \#{locale_date(user.register_number_start_date, \"«%d» %B %Y\", :ua)}\"\r\n        - else\r\n          %p= \"Паспорт номер: \#{user.passport_number}\"\r\n          %p= \"Виданий \#{ua_user_field.passport_issued_by}\"\r\n        %br\r\n        %p= \"п/р № \#{ua_user_field.c_a_number}\"\r\n        %p= ua_user_field.bank_name\r\n        %p= \"Адреса банку: \#{ua_user_field.bank_address}\"\r\n        %p= \"Bank SWIFT Code \#{user.swift_code}\"\r\n        %p= \"IBAN \#{user.iban_number}\" if user.iban_number.present?\r\n        %br\r\n        %br\r\n        %p\r\n          \#{person_type_for_locale(person_type, :ua)}__________________/\r\n          \#{ua_user_field.name_with_initials}\r\n        %br\r\n      %td.padding_top_11\r\n        %p.bold The Contractor\r\n        %p= \"\#{person_type_for_locale(person_type, :en)} \#{en_user_field.full_name}\"\r\n        %br\r\n        %p= \"Address: \#{en_user_field.address}\"\r\n        %br\r\n        %p= \"Taxpayer ID: \#{user.taxpayer_id}\"\r\n        %br\r\n        - if person_type == \"PE\"\r\n          %p= \"Register № \#{user.register_number}\"\r\n          %p= \"As of \#{locale_date(user.register_number_start_date, \"%B %d, %Y\", :en)}\"\r\n        - else\r\n          %p= \"Passport number: \#{user.passport_number}\"\r\n          %p= \"Issued by \#{en_user_field.passport_issued_by}\"\r\n        %br\r\n        %p= \"c/a № \#{en_user_field.c_a_number}\"\r\n        %p= en_user_field.bank_name\r\n        %p= \"Bank Address: \#{ua_user_field.bank_address}\"\r\n        %p= \"Bank SWIFT Code \#{user.swift_code}\"\r\n        %p= \"IBAN \#{user.iban_number}\" if user.iban_number.present?\r\n        %br\r\n        %br\r\n        %p\r\n          \#{person_type_for_locale(person_type, :en)}__________________/\r\n          \#{en_user_field.short_name}\r\n        %br\r\n"
    end
  end
end
